import React from 'react'

const TopMenu = () => {
  const localStorage = window.localStorage

  const name = localStorage.getItem('name')

  const handleLogout = () => {
    localStorage.clear()
    return window.location.href = '/'
  }

  return (
    <div className='topMenu'>
      <p>Hello, {name}</p>
      <p onClick={handleLogout}>Logout</p>
    </div>
  )
}

export default TopMenu
