import React, { Component } from 'react';
import axios from 'axios';

import Countdown from './Countdown';

export default class CountdownContainer extends Component {
    state = {
        events: [],
        amountOfEvents: 0
    }

    checkAmount = (amount) => {
        let gridAmount = 0
        if (amount = 2) {
             gridAmount = 2;
        } else if (amount = 1) {
            return gridAmount = 1;
        } else {
            gridAmount = 3
        }
    }

    fetchEvents = () => {
        const encodedURI = window.encodeURI(this.props.uri);
        return axios.get(encodedURI).then(res => {
            this.setState(() => {
                return {
                    events: res.data,
                    amountOfEvents: res.data.length
                }
            }, this.checkAmount(this.state.amountOfEvents)
            );
        });
    }

    componentDidMount() {
        this.fetchEvents();
    }

    render() {
        console.log(this.state.amountOfEvents);
        return (
            <div className="App">
                <h1>Stuff be happening!</h1>
                <div className="countdownContainerGrid">
                    {this.state.events.map(event =>
                        <Countdown
                            key={event.Id}
                            title={event.Title}
                            month={event.Month}
                            day={event.Day}
                            year={event.Year}
                            image={event.Background}
                        />
                    )}
                </div>
            </div>
        )
    }
}
