import React, { Component } from 'react';

export default class Countdown extends Component {

    render() {
        let seconds = 60;
        while (seconds > 0) {
            setInterval(() => {
                console.log(seconds--)
            }, 1000);
        }

        let date = new Date();
        let dateMili = date.getTime();
        let event = new Date(`${this.props.month}/${this.props.day}/${this.props.year}`);
        let eventMili = event.getTime();
        let diffMili = eventMili - dateMili;
        let days = Math.floor(diffMili / (1000 * 60 * 60 * 24));
        let sec = date.getSeconds(diffMili);
        console.log(sec);
        return (
            <div className="cdComp">
                <h1>{this.props.title}</h1>
                <h2>{days} days</h2>
                <h4>{this.props.month}/{this.props.day}/{this.props.year}</h4>
            </div>
        )
    }
}
