import React, { Component } from 'react';
import axios from 'axios';

import Countdown from './Countdown';

class CountdownContainer extends Component {
    state = {
        events: [],
        amountOfEvents: 0,
        countdownContainerGrid: "countdownContainerGrid",
        deletedEvent: false,
        newEvent: this.props.newEvent
    }

    getSomeEvents = () => {
        this.setState({ 
            events: this.props.events,
            amountOfEvents: this.props.events.length
        }, () => {
            this.howManyEvents(this.state.amountOfEvents)
        })
    }

    getNewComment = commentInfo => {
        this.setState({
            commentBtnText: 'Comment',
            showCommentForm: !this.state.showCommentForm,
            comments: [...this.state.comments.reverse(), commentInfo],
        });
    }

    howManyEvents = amountOfEvents => {
        if (this.state.amountOfEvents === 2) {
            this.setState({ countdownContainerGrid: "countdownContainerGrid2" })
        } else if (this.state.amountOfEvents === 1) {
            this.setState({ countdownContainerGrid: "countdownContainerGrid1" })
        }
    }

    newEvent = eventInfo => {
        this.setState({
            events: [...this.state.events.reverse(), eventInfo]
        });
    }

    deleted = eventId => {
        axios.delete('http://localhost:3001/events/delete', { params: { eventId } })
            .then(async (response) => {
                // const res = await fetch(`http://localhost:3001/events`);
                // const events = await res.json();
                const events = this.state.events.filter((value, index, arr) => {
                    return value._id !== eventId;
                })
            });
    }

    render() {
        if (this.state.events.length < this.props.events.length) {
            this.getSomeEvents()
        }
        let events = this.state.events;
        return (
            <div className="App">
                <h1>Stuff be happening!</h1>
                {
                    this.state.events.length > 0
                        ?
                        <div className={this.props.countdownContainerGrid}>
                            {events.map(event =>
                                <Countdown
                                    deleted={this.deleted}
                                    key={event._id}
                                    id={event._id}
                                    title={event.title}
                                    month={event.month}
                                    day={event.day}
                                    year={event.year}
                                    image={event.background}
                                />
                            )}
                        </div>
                        :
                        <div></div>
                }
            </div>
        )
    }
}

export default CountdownContainer;
