import React from 'react';
import './App.css';
import Countdown from './components/Countdown';
import EventCreator from './components/EventCreator';

const events = require("./countdown-list.json");

function App() {



  function check(event) {
    if (event.length === 0) {
      return res =
        <div className="App">
          <h1>Make a new event!</h1>
        </div>
    } else {
      return res =
        <div className="App">
          <h1>Stuff be happening!</h1>
          {events.map(event =>
              <Countdown
                key={event.id}
                title={event.title}
                month={event.month}
                day={event.day}
                year={event.year}
                image={event.background}
              />
          )}
          <EventCreator />
        </div>
    }
  }

  var res = null;
  let getEvents = (events.map(event => {
    return event
  }
  ));
  check(getEvents);
  return res;
}

export default App;
