import React from 'react';
import { BrowserRouter as Router, Link, Route } from 'react-router-dom';

import './App.css';

import Countdown from './components/Countdown';
import EventCreator from './components/EventCreator';

const events = require("./countdown-list.json");

function App() {

  function check(event) {
    if (event.length === 0) {
      return res =
        <Router>
          <div className="App">
            <Link to="/add-an-event">Add an event!</Link>
            <Route exact path="/add-an-event" component={EventCreator} />
          </div>
        </Router>
    } else {
      return res =
        <Router>
          <div className="addEventBtn">
            <Link to="/add-an-event">Add an event!</Link>
          </div>
          <Route exact path="/add-an-event" component={EventCreator} />
          <div className="App">
            <h1>Stuff be happening!</h1>
            {events.map(event =>
              <Countdown
                key={event.id}
                title={event.title}
                month={event.month}
                day={event.day}
                year={event.year}
                image={event.background}
              />
            )}
          </div>
        </Router>
    }
  }

  var res = null;

  let getEvents = (events.map(event => {
    return event
  }));

  check(getEvents);

  return res;
}

export default App;
