import React from 'react';
import { BrowserRouter as Router, Link, Route } from 'react-router-dom';

import './App.css';

// import Countdown from './components/Countdown';
import EventCreator from './components/EventCreator';
import CountdownContainer from './components/CountdownContainer';

const events = require("./countdown-list.json");

function App() {

  function check(event) {
    if (event.length === 0) {
      return res =
          <div className="App">
            <EventCreator />
          </div>
    } else {
      return res =
        <Router>
          <div className="addEventBtn">
            <Link style={{ textDecoration: 'none', color: 'black' }} to="/add-an-event">Add an event!</Link>
          </div>
          <Route exact path="/add-an-event" component={EventCreator} />
          <CountdownContainer />
        </Router>
    }
  }

  var res = null;

  let getEvents = (events.map(event => {
    return event
  }));

  check(getEvents);

  return res;
}

export default App;
