import React, { Component } from 'react';
import axios from 'axios';
import { Link, withRouter } from 'react-router-dom';

import closeIcon from '../assets/images/close-cross-in-circular-outlined-interface-button-2.png';

let bg = ""
let bgColor = {}

class Countdown extends Component {
    constructor(props) {
        super(props)
        this.state = {
            second: 0
        }
    }

    // interval = setInterval(() => {
    //     this.setState({ second: this.state.second + 1 })
    // }, 1000);

    checkForBackground() {
        if (this.props.image === "") {
            bg = "";
            bgColor = {
                background: ""
            };
        } else {
            bg = this.props.image;
            bgColor = {
                background: "none"
            };
        }
    }

    deleteEvent = () => {
        axios.delete(`http://localhost:3001/events/${this.props.id}`)
            .then(res => console.log(res));
        this.props.deleted();
    }

    render() {

        this.checkForBackground();

        const event = new Date(`${this.props.month}/${this.props.day}/${this.props.year}`).getTime();
        let date;
        let diff;
        let days;
        let hours;
        let minutes;
        let seconds;

        setInterval(() => {
            date = new Date().getTime();
            diff = event - date;

            days = Math.floor(diff / (1000 * 60 * 60 * 24));
            hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
            seconds = Math.floor((diff % (1000 * 60)) / 1000);
        }, 1000);



        return (
            <div className="eventContainer">
                <div className="imgContainer">
                    <img src={bg} alt="background" ></img>
                </div>
                <div className="textContainer" style={bgColor}>
                    <Link to="/">
                        <img onClick={this.deleteEvent} src={closeIcon} alt="close"></img>
                    </Link>
                    <div className="text">
                        <h1>{this.props.title}</h1>
                        <h4>{this.props.month}/{this.props.day}/{this.props.year}</h4>
                        <h2>{days}:{hours}:{minutes}:{seconds}</h2>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(Countdown);
